import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';




function Login()
{
    const navigate = useNavigate();

    const [email, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const navigateHome = () => 
    {
        navigate('/home');
    }

    const doLogin = async event => 
    {
        event.preventDefault();

        alert('Logging in!!');
        navigateHome();
    };

const signUp = () => 
{
    navigate('/signUp');
}
const handleApi = async () => {
    

    
    console.log(email, password);
    /*axios.post('https://gainzboy.herokuapp.com/auth/login' ,{
        email : email ,
        password : password
    } )
        .then(response => {    
            console.log(response.data)
        alert('Success')
    } )
        .catch(error => {
        alert('service error')
        console.log(error);
    } );*/
    var item = { email : email.trim() , password : password.trim()};

    var js = JSON.stringify(item);

    const result = await fetch ("https://gainzboy.herokuapp.com/auth/login" , {
        method : 'POST',
        body : js ,
        headers : {
            "Content-Type" : "application/json" }     
    });
    var res = JSON.parse(await result.text());
    if (res.errorMessage != undefined) {
        setTextError(res.errorMessage);
    }
    else {
        global.userId = res.userID;
        global.token = res.token;
        global.fullName = res.fullname;
        global.email = email.trim();
        global.password = password.trim();

    }

}
    return(
      <div class="box">
        <div id="loginDiv" class ="BoxContainer">
            <form class ="FormContainer">
            <span id="inner-title"><h1>GAINZ<span class="textLogo">BOY</span></h1></span><br />
            <input type="text" id="loginName" placeholder="email" 
            onChange={(e)=>setUsername(e.target.value)}
            /><br />
            <input type="password" id="loginPassword" placeholder="password" 
            onChange={(e)=>setPassword(e.target.value)}/><br />
            </form>
            <button onClick={handleApi} className="loginButton"> Login</button>
           
            <button onClick={signUp} className="loginButton"> Sign Up</button>
        </div>
    </div>  
      
    );
};

export default Login;
