import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


function Login()
{
    const navigate = useNavigate();

    const [email, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const navigateHome = () => 
    {
        navigate('/home');
    }

    const doLogin = async event => 
    {
        event.preventDefault();

        alert('Logging in!!');
        navigateHome();
    };


const handleApi = () => {
    console.log(email, password);
    /*axios.post('https://gainzboy.herokuapp.com/auth/login' ,{
        email : email ,
        password : password
    } ).then(response => {    
        console.log(response.data)
        alert('Success')
    } ).catch(error => {
        alert('service error')
        console.log(error);
    } );*/
    let item = { email, password};
    let result =  fetch ("https://gainzboy.herokuapp.com/auth/login" , {
        method : 'POST',
        headers : {
            "Content-Type" : "application/json",
            "Accept" : "application/json"
        },
        body: JSON.stringify(item)
    });
    result = result.json();
    localStorage.setItem("user-info", JSON.stringify(result))
    navigate('/home');
}
    return(
      <div className="box">
        <div id="loginDiv" class ="BoxContainer">
            <form class ="FormContainer">
            <span id="inner-title"><h1>GAINZ<span class="textLogo">BOY</span></h1></span><br />
            <input type="text" id="loginName" placeholder="email" 
            onChange={(e)=>setUsername(e.target.value)}
            /><br />
            <input type="password" id="loginPassword" placeholder="password" 
            onChange={(e)=>setPassword(e.target.value)}/><br />
            </form>
            <button onClick={handleApi} className="loginButton"> Login</button>
           
            <button onClick={navigate('/signUp')} className="loginButton"> Login</button>
        </div>
    </div>  
      
    );
};

export default Login;
