import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function Login()
{
    const navigate = useNavigate();

    const [email, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const navigateHome = () => 
    {
        navigate('/home');
    }

    const doLogin = async event => 
    {
        event.preventDefault();

        alert('Logging in!!');
        navigateHome();
    };

async function login()
{
    console.warn(email,password)
    let item = {email, password};
    let result = await fetch("./auth/login", {
        method: 'POST' ,
        headers: {
            "Content-type" : "application/json" ,
            "Accept" : "application/json" 
        },
        bodyn : JSON.stringify(item)
    });
    result = await result.json();
    localStorage.setItem( "user-info" , JSON.stringify(result));
    navigateHome();
}
    return(
      <div class="box">
        <div id="loginDiv" class ="BoxContainer">
            <form class ="FormContainer" onSubmit={login}>
            <span id="inner-title"><h1>GAINZ<span class="textLogo">BOY</span></h1></span><br />
            <input type="text" id="loginName" placeholder="email" 
            onChange={(e)=>setUsername(e.target.value)}
            /><br />
            <input type="password" id="loginPassword" placeholder="password" 
            onChange={(e)=>setPassword(e.target.value)}/><br />
            <button onClick={login} className="loginButton"> Login</button>
            </form>
            <a href="/signUp" class="NewAccount">Create an Account</a>
            <span id="loginResult"></span>
        </div>
    </div>  
      
    );
};

export default Login;
