import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


function Login()
{
    const navigate = useNavigate();

    const [email, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const navigateHome = () => 
    {
        navigate('/home');
    }

    const doLogin = async event => 
    {
        event.preventDefault();

        alert('Logging in!!');
        navigateHome();
    };


const handleApi = () => {
    console.log(email, password);
    axios.get('https://gainzboy.herokuapp.com/auth/login',{
        email : email ,
        password : password
    } ).then(response => {    
        console.log(response.data)
        alert('Success')
    } ).catch(error => {
        alert('service error')
        console.log(error);
    } );
}
    return(
      <div class="box">
        <div id="loginDiv" class ="BoxContainer">
            <form class ="FormContainer">
            <span id="inner-title"><h1>GAINZ<span class="textLogo">BOY</span></h1></span><br />
            <input type="text" id="loginName" placeholder="email" 
            onChange={(e)=>setUsername(e.target.value)}
            /><br />
            <input type="password" id="loginPassword" placeholder="password" 
            onChange={(e)=>setPassword(e.target.value)}/><br />
            </form>
            <button onClick={handleApi} className="loginButton"> Login</button>
           
            <a href="/signUp" className="NewAccount">Create an Account</a>
            <span id="loginResult"></span>
        </div>
    </div>  
      
    );
};

export default Login;
